  //验证手机格式
  function validatePhone () {
    let phone = '';
    console.log("$('.phone')",$('.phone'));

    if($('#consultModal').css('display') == 'none'){
      phone = $('.phone')[0].value;
    }else{
      phone = $('.phone')[1].value;
    }

    console.log('phone',phone);
    if (!phone) {
      layer.msg('请输入手机号码',{icon:2});
      return false;
    }
    if (phone.length != 11) {
      layer.msg('手机号码格式不正确',{icon:2});
      return false;
    }
    return true;
  }
  //获取验证码
  let intervalID = '';
  function getCode (e){
    let outTime = 60;
    console.log($(e));
    if(!validatePhone()){
      return
    }
    if(intervalID){
      return;
    }
    intervalID = setInterval(function(){
      if(outTime == 0){
        outTime = 60;
        clearInterval(intervalID)
        $(e).html('获取验证码');
        return
      }
      outTime--;
      $(e).html(outTime+'S后可重新获取');
    },1000)
    layer.msg('验证码发送成功',{icon:1});
  }
  function login(){
    validatePhone();

  }
